function  simulation_execute(){
  for (var i=0; i < simproccessor.length; ++i){
      simproccessor[i].execute();
  }
}

function simulation_detach(){
  for (var i=0; i < simproccessor.length; ++i){
      simproccessor[i].detach();
  }
}





window.simTick = setInterval(simulation_execute, SIMULATIONTICK );




